mapper.update:
UPDATE Trade
TradeName = #{pojo.tradeName}, TradePhone = #{pojo.tradePhone}, TradeState = #{pojo.tradeState}, CreateTime = #{pojo.createTime}, EndTime = #{pojo.endTime}, TradeRemark = #{pojo.tradeRemark}, TradeLocation = #{pojo.tradeLocation}, TradePicIdList = #{pojo.tradePicIdList,typeHandler=ListTypeHandler}, UserId = #{pojo.user.userId},
WHERE TradeId = #{pojo.tradeId}

mapper.select:
SELECT 
FROM Trade

     AND TradeId = #{pojo.tradeId} 
     AND TradeName = #{pojo.tradeName} 
     AND TradePhone = #{pojo.tradePhone} 
     AND TradeState = #{pojo.tradeState} 
     AND CreateTime = #{pojo.createTime} 
     AND EndTime = #{pojo.endTime} 
     AND TradeRemark = #{pojo.tradeRemark} 
     AND TradeLocation = #{pojo.tradeLocation} 
     AND TradePicIdList = #{pojo.tradePicIdList,typeHandler=ListTypeHandler} 
     AND UserId = #{pojo.user.userId}

SELECT 
FROM Trade


         AND TradeId = #{page.condition.tradeId} 
         AND TradeName = #{page.condition.tradeName} 
         AND TradePhone = #{page.condition.tradePhone} 
         AND TradeState = #{page.condition.tradeState} 
         AND CreateTime = #{page.condition.createTime} 
         AND EndTime = #{page.condition.endTime} 
         AND TradeRemark = #{page.condition.tradeRemark} 
         AND TradeLocation = #{page.condition.tradeLocation} 
         AND TradePicIdList = #{page.condition.tradePicIdList,typeHandler=ListTypeHandler} 
         AND UserId = #{page.condition.user.userId} 


ORDER BY TradeId DESC
LIMIT #{page.startIndex}, #{page.pageSize}

SELECT count(0)
FROM Trade


         AND TradeId = #{page.condition.tradeId} 
         AND TradeName = #{page.condition.tradeName} 
         AND TradePhone = #{page.condition.tradePhone} 
         AND TradeState = #{page.condition.tradeState} 
         AND CreateTime = #{page.condition.createTime} 
         AND EndTime = #{page.condition.endTime} 
         AND TradeRemark = #{page.condition.tradeRemark} 
         AND TradeLocation = #{page.condition.tradeLocation} 
         AND TradePicIdList = #{page.condition.tradePicIdList,typeHandler=ListTypeHandler} 
         AND UserId = #{page.condition.user.userId}
