mapper.update:
UPDATE Trade
StoreName = #{pojo.storeName}, StoreType = #{pojo.storeType}, StorePhone = #{pojo.storePhone}, StoreRemark = #{pojo.storeRemark}, StoreLocation = #{pojo.storeLocation}, StoreState = #{pojo.storeState}, CreateTime = #{pojo.createTime}, EndTime = #{pojo.endTime}, StorePicIdList = #{pojo.storePicIdList,typeHandler=ListTypeHandler}, UserId = #{pojo.user.userId}, TradeId = #{pojo.trade.tradeId},
WHERE StoreId = #{pojo.storeId}

mapper.select:
SELECT 
FROM Store

     AND StoreId = #{pojo.storeId} 
     AND StoreName = #{pojo.storeName} 
     AND StoreType = #{pojo.storeType} 
     AND StorePhone = #{pojo.storePhone} 
     AND StoreRemark = #{pojo.storeRemark} 
     AND StoreLocation = #{pojo.storeLocation} 
     AND StoreState = #{pojo.storeState} 
     AND CreateTime = #{pojo.createTime} 
     AND EndTime = #{pojo.endTime} 
    AND StorePicIdList = #{pojo.storePicIdList,typeHandler=ListTypeHandler} 
     AND UserId = #{pojo.user.userId} 
     AND TradeId = #{pojo.trade.tradeId}

SELECT 
FROM Store


         AND StoreId = #{page.condition.storeId} 
         AND StoreName = #{page.condition.storeName} 
         AND StoreType = #{page.condition.storeType} 
         AND StorePhone = #{page.condition.storePhone} 
         AND StoreRemark = #{page.condition.storeRemark} 
         AND StoreLocation = #{page.condition.storeLocation} 
         AND StoreState = #{page.condition.storeState} 
         AND CreateTime = #{page.condition.createTime} 
         AND EndTime = #{page.condition.endTime} 
        AND StorePicIdList = #{page.condition.storePicIdList,typeHandler=ListTypeHandler} 
         AND UserId = #{page.condition.user.userId} 
         AND TradeId = #{page.condition.trade.tradeId} 


ORDER BY StoreId DESC
LIMIT #{page.startIndex}, #{page.pageSize}

SELECT count(0)
FROM Store


         AND StoreId = #{page.condition.storeId} 
         AND StoreName = #{page.condition.storeName} 
         AND StoreType = #{page.condition.storeType} 
         AND StorePhone = #{page.condition.storePhone} 
         AND StoreRemark = #{page.condition.storeRemark} 
         AND StoreLocation = #{page.condition.storeLocation} 
         AND StoreState = #{page.condition.storeState} 
         AND CreateTime = #{page.condition.createTime} 
         AND EndTime = #{page.condition.endTime} 
        AND StorePicIdList = #{page.condition.storePicIdList,typeHandler=ListTypeHandler} 
         AND UserId #{page.condition.user.userId} 
         AND TradeId = #{page.condition.trade.tradeId}
